// SPDX-License-Identifier: MIT
pragma solidity >=0.6.6;
pragma abicoder v2;

// import '@openzeppelin/contracts/math/SafeMath.sol';
import "@uniswap/v3-core/contracts/libraries/LowGasSafeMath.sol";
import "./utils/FlashLoanReceiverBaseV2.sol";
import "./utils/Withdrawable.sol";
import "./UniQuick.sol";

contract AaveUniQuick is FlashLoanReceiverBaseV2, Withdrawable, UniQuick {
    using LowGasSafeMath for uint256;

    constructor( 
    address _AaveaddressProvider,
    address _uniswapV3Router,
    address _sushiswapRouter,
    address _uniswapV3factory,
    address _sushiswapfactory,
    // address _WETH9,
    address _quickswapV2RouterAddress
    ) 
    FlashLoanReceiverBaseV2(_AaveaddressProvider) 
    UniQuick(_uniswapV3Router, _sushiswapRouter, _uniswapV3factory, _sushiswapfactory, _quickswapV2RouterAddress){}


    // struct ConstructorArguments {
    //     address _AaveaddressProvider;
    //     address _uniswapV3Router;
    //     address _sushiswapRouter;
    //     address _uniswapV3factory;
    //     address _sushiswapfactory;
    //     address _WETH9;
    //     address _quickswapV2RouterAddress;
    // }

    event AaveBorrowDetails(
        address indexed _borrowedAsset,
        uint indexed _amount,
        uint _premium
    );

    function executeOperation(
            address[] calldata assets,
            uint256[] calldata amounts,
            uint256[] calldata premiums,
            address /* initiator */,
            bytes calldata params 
        )
            external
            override
            returns (bool)
        {
            address borrowedAsset = assets[0];
            uint borrowedAmount = amounts[0];
            uint premiumAmount = premiums[0];
            
            // This contract now has the funds requested.
            // Your logic goes here.
            require(msg.sender == address(LENDING_POOL), "Not pool");
            emit AaveBorrowDetails(borrowedAsset, borrowedAmount, premiumAmount);

            (FlashParams memory decoded) = abi.decode(params, (FlashParams));
            //If you borrowed from Aave, proceed to also start UniswapQuickswaps BUT this time, you
            //have the borrowed asset from Aave in this contract.
            startUniswapV3AndQuickSwaps(decoded);

            // At the end of your logic above, this contract owes
            // the flashloaned amounts + premiums.
            // Therefore ensure your contract has enough to repay
            // these amounts.
            
            // Approve the LendingPool contract allowance to *pull* the owed amount
            //Use this logic when you have multiple assets
            // for (uint i = 0; i < assets.length; i++) {
            //     uint amountOwing = amounts[i].add(premiums[i]);
            //     IERC20(assets[i]).approve(address(LENDING_POOL), amountOwing);
            // }

            uint amountOwing = borrowedAmount.add(premiumAmount);
            IERC20(borrowedAsset).approve(address(LENDING_POOL), amountOwing);        
            return true;
        }


        // Flash multiple assets 
        // function flashloan(address[] memory assets, uint256[] memory amounts) public onlyOwner {
        //     _flashloan(assets, amounts);
        // }

        /*
        *  Flash loan 1,000,000,000,000,000,000 wei (1 ether) worth of `_asset`
        */
        function startTransaction(FlashParams memory _data) public onlyOwner {

            if(_data.AaveBorrow){

                address _borrowAsset = _data.token0;
                uint256 _borrowAmount = _data.amount0;

                bytes memory params = abi.encode(_data);

                address[] memory assets = new address[](1);
                assets[0] = _borrowAsset;

                uint256[] memory amounts = new uint256[](1);
                amounts[0] = _borrowAmount;

                _flashloan(assets, amounts, params);
            }else{
                //If you didn't borrow from Aave, just start a UniswapAndQuickSwaps.
                startUniswapV3AndQuickSwaps(_data);
            }

        }


        function _flashloan(address[] memory assets, uint256[] memory amounts, bytes memory params) internal {
            //We send the flashloan amount to this contract (receiverAddress) so we can make the arbitrage trade

            address receiverAddress = address(this);

            address onBehalfOf = address(this);
            // bytes memory params = "";
            uint16 referralCode = 0;

            uint256[] memory modes = new uint256[](assets.length);

            // 0 = no debt (flash), 1 = stable, 2 = variable
            for (uint256 i = 0; i < assets.length; i++) {
                modes[i] = 0;
            }

            LENDING_POOL.flashLoan(
                receiverAddress,
                assets,
                amounts,
                modes,
                onBehalfOf,
                params,
                referralCode
            );
        }

    }

